/* eslint-disable no-unused-expressions */

import React from "react";
import './Toolbar.css'


const Toolbar = (props) => {



    if (props.activePage === "HomePage") {
        setHome("isActive");
        setBlog("false");
        setSubmit("false");
    }

    return (
        <div className="ToolbarContainer">
            <div class="Dropdown-menu">Menu</div>
                <a className={`h1 ${props.activePage}`} href="http://localhost:3000/">Home</a>
                <a className={`h1 ${blog}`} href="http://localhost:3000/blog">Blogs</a>
                <a className={`h1 ${submit}`} href="http://localhost:3000/submitblog">Submit Blog</a>
        </div>
    )
}

export default Toolbar;
