import React from "react";
import './DBPostForm.css'

const DBPostForm = () => {

    const user = {
        name: "Adam Heeps",
        city: "Dunblane"
    }

    return (
        <div className="DBPostForm">
            <h3>Post a record to the database:</h3>
            <form>
            <label>
                Name: <input type="text" value={this.user.name} />
                City: <input type="text" value={this.state.city} />
            </label>
            <input type="submit" value="Submit" />
            </form>
        </div>
)}

export default DBPostForm;
