import React from "react";
import './Toolbar.css'


const Toolbar = (props) => {

    const [home, setHome] = React.useState("false");
    const [blog, setBlog] = React.useState("false");
    const [submit, setSubmit] = React.useState("false");

    //     if (props.activePage === "HomePage") {
    //         setHome("isActive");
    // };

    return (
        <div className="ToolbarContainer">
            <div class="Dropdown-menu">Menu</div>
                <a className={(p) => {
                    if (props.activePage === "HomePage") {
                        setHome("isActive")
                        setBlog("false")
                        setSubmit("false")
                        return home }
                    }} href="http://localhost:3000/">Home</a>
                <a className={(p) => {
                    if (props.activePage === "BlogPage") {
                        setHome("false")
                        setBlog("false")
                        setSubmit("false")
                        return home }
                    }} href="http://localhost:3000/blog">Blogs</a>
                <a className={(p) => {
                    if (props.activePage === "SubmitPage") {
                        setHome("false")
                        setBlog("false")
                        setSubmit("false")
                        return home }
                    }} href="http://localhost:3000/submitblog">Submit Blog</a>
        </div>
    )
}

export default Toolbar;
